import gsap from "gsap"
import Observer from "gsap/Observer"

const navTl = gsap.timeline({
    duration: {default: 0.2}
})


// Open Nav
export const openNav = ()=>{
    gsap.fromTo('.navBox', {
        duration: 0.2,
        opacity: 0,
        display: "none",
        delay: 0.1
    }, {
        duration: 0.2,
        opacity: 1,
        display: "grid",
        delay: 0.1
    })
    gsap.to(".burger.burger-one", {
        // x: "10%",
        // rotate: '45deg'
        transform: 'rotate(45deg) translate(25%, 10%)'
    })
    gsap.to(".burger.burger-two", {
        // rotate: '-45deg',
        transform: 'rotate(-45deg) translateX(25%)'
    })
    gsap.fromTo(".navBox li", {
        x: "25%",
        opacity:0,
        transform: 'skewX(10deg)',
        delay: 0.2
    },{
        x: "0%",
        opacity: 1,
        transform: 'skewX(0deg)',
        delay: 0.2
    })
    gsap.fromTo(".navBox li", {
        width: 0
    },{
        width: "100%",
        borderBottom: "1px solid #fff",
        delay: 0.8
    })
    gsap.fromTo(".navBox li:nth-child(1)", {
        width: 0
    },{
        width: "100%",
        borderTop: "1px solid #fff",
        delay: 0.8
    })
}


// Close Nav
export const closeNav = ()=>{
    navTl.to(".navBox li", {
        x: "25%",
        opacity:0,
        transform: 'skewX(10deg)',
        delay: 0.1
    })
    .to('.navBox', {
        duration: 0.2,
        opacity: 0,
        display: "none",
        delay: 0.2
    })
    gsap.to(".burger.burger-one", {
        transform: 'rotate(0) translate(0%, 0%)',
        delay: 0.2
    })
    gsap.to(".burger.burger-two", {
        // rotate: '0deg',
        transform: 'rotate(0) translateX(25%) translateX(-25%)',
        delay: 0.2
    })
}


