import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import './header.css'
import { openNav, closeNav, gradientHover } from '../../modules/header'
import gsap from 'gsap'


const Header = (props) => {
  // Gradient Hover Starts
  function gradientHover() {
    var navLi = document.querySelectorAll(".navBox li")
    navLi.forEach(navLi => {
      navLi.addEventListener("mouseover", ()=>{
        gsap.fromTo(navLi, {
          duration: 0.5,
          backgroundSize: '0% 100%',
          delay: 0.2
        },{
          duration: 0.1,
          backgroundSize: '100% 100%',
        })
      })
      navLi.addEventListener("mouseout", ()=>{
        gsap.fromTo(navLi, {
          duration: 0.1,
          backgroundSize: '100% 100%',
          delay: 0.2
        },{
          duration: 0.5,
          backgroundSize: '0% 100%',
        })
      })
    });
  }
  gradientHover()
  // Gradient Hover Ends

  const [isMobile, setIsMobile] = useState(false);
  return (
    <header>
      <div className="logoBox">
        <span><Link id='logo' to="/">{props.logoText}</Link></span>
      </div>
      <div className="navBox">
        <ul>
          <li><Link to="/">Home</Link></li>
          <li><Link to="about">About</Link></li>
          <li><Link to="projects">Projects</Link></li>
          <li><Link to="contact">Contact</Link></li>
        </ul>
      </div>
      <div className="burgerMenu">
        <span className='burgerBtn' onClick={() => { setIsMobile(!isMobile); isMobile ? closeNav() : openNav() }}>
          <div className="burger burger-one"></div>
          <div className="burger burger-two"></div>
        </span>
      </div>
    </header>
  )
}

Header.defaultProps = {
  logoText: "Logo"
}


export default Header



