import { React, useEffect, useState } from 'react'
import gsap from 'gsap'
import Client from './Client'
import beyonce from 'img/clients/beyonce1.png'
import eminem from 'img/clients/eminem.png'
import jcole from 'img/clients/jcole1.png'
import theweekend from 'img/clients/theweekend1.png'
import nas from 'img/clients/nas.png'
import { hover } from '@testing-library/user-event/dist/hover'

const Clients = () => {
    const [hovered, setHovered] = useState(false);
    
    useEffect(() => {
        var clientImgs = document.querySelectorAll(".clients-row img")
        clientImgs.forEach(clientImg=>{
            clientImg.addEventListener("mouseover", ()=> setHovered(true))
            clientImg.addEventListener("mouseout", ()=> setHovered(false))
            clientImg.addEventListener("mouseover", ()=> console.log({hovered}))
        })
    });

        // useEffect(() => {
        //     var clientImgs = document.querySelectorAll(".clients-row img")
        // clientImgs.forEach(clientImg=>{
        //     clientImg.addEventListener("mouseover", ()=>{
        //         gsap.to(clientImg, {
        //             filter: "invert(1)"
        //         })
        //     })
        //     clientImg.addEventListener("mouseout", ()=>{
        //         gsap.to(clientImg, {
        //             filter: "contrast(0.5)"
        //         })
        //     })
        // })
        // });

        return (
            <div className='content' id='clients'>
                <div className="clients-row">
                    <Client img={beyonce} />
                    <Client img={eminem} />
                    <Client img={jcole} />
                    <Client img={theweekend} />
                    <Client img={nas} />
                </div>
            </div>
        )
    }



// function helloWorld(){

// }

// helloWorld()

export default Clients
