import gsap from "gsap"

const navTl = gsap.timeline({
    duration: {default: 0.2}
})

export const openNav = ()=>{
    gsap.fromTo('.navBox', {
        duration: 0.2,
        opacity: 0,
        display: "none",
        delay: 0.1
    }, {
        duration: 0.2,
        opacity: 1,
        display: "grid",
        delay: 0.1
    })
    gsap.fromTo(".navBox li", {
        x: "25%",
        opacity:0,
        transform: 'skewX(10deg)',
        delay: 0.2
    },{
        x: "0%",
        opacity: 1,
        transform: 'skewX(0deg)',
        delay: 0.2
    })
}

export const closeNav = ()=>{
    navTl.to(".navBox li", {
        x: "10%",
        opacity:0,
        transform: 'skewX(10deg)',
        delay: 0.1
    })
    .to('.navBox', {
        duration: 0.2,
        opacity: 0,
        display: "none",
        delay: 0.2
    })
}
