import gsap from "gsap"

const navTl = gsap.timeline({
    duration: {default: 0.2}
})

export const openNav = ()=>{
    gsap.fromTo('.navBox', {
        duration: 0.2,
        opacity: 0,
        display: "none",
        delay: 1
    }, {
        duration: 0.2,
        opacity: 1,
        display: "grid",
        delay: 1
    })
    gsap.to(".navBox a", {
        opacity: 1,
        delay: 0.5
    })
    gsap.from(".navBox a", {
        scale: 1.5,
        delay: 0.8
    })
}

export const closeNav = ()=>{
    navTl.to('.navBox', {
        duration: 0.2,
        opacity: 0,
        display: "none"
    })
}
