import gsap from "gsap"
import Observer from "gsap/Observer"

const navTl = gsap.timeline({
    duration: {default: 0.2}
})

navTl.fromTo('.navBox', {
    duration: 0.2,
    opacity: 0
}, {
    duration: 0.2,
    opacity: 1,
    display: "grid"
})
navTl.paused()

export const openNav = ()=>{
    navTl.play()
}

export const closeNav = ()=>{
    navTl.reverse()
}
